<?php $this->lang->load('comments/comments'); ?><h3><?php echo lang('comments.title');?></h3>		<fieldset id="comments">	<legend><?php echo lang('comments.user_comments');?></legend>		<?php $comments = $this->comments_m->get_comments(array('module' => $module, 'module_id' => $module_id, 'is_active' => 1));	if(!empty($comments)): ?>		{literal}	<ul>	<?php foreach($comments as $comment): ?>		<li class="comment">			<?php echo gravatar($comment->email,40); ?>						<strong class="author">				<?php if($comment->website): ?>					<?php echo anchor($comment->website, $comment->name); ?>				<?php else: ?>					<?php echo $comment->name; ?>				<?php endif;?>			</strong>			<p class="date"><?php echo date('M d, Y', $comment->created_on); ?></p>			<div class="body">				<p><?php echo nl2br(stripslashes($comment->comment));?></p>			</div>		</li>	<?php endforeach; ?>	</ul>	{/literal}	<?php else: ?>	<p><?php echo lang('comments.no_comments');?></p><?php endif; ?>	</fieldset>								<fieldset id="write-comment">	<legend><?php echo lang('comments.your_comment');?></legend>	<?php echo $this->load->view('comments/form', array('module'=>$module, 'id' => $module_id)); ?> </fieldset>